import styled from 'styled-components';

export const Circle = styled.div`
    display: flex;
    position: absolute;
    left: 2px;
    top: 2px;
    background-color:#E94F37;
    border-radius: 50%;
    opacity: 0.85;
    width: 45px;
    height: 45px;
    border: 1px solid black;
`;

export const DiscountText = styled.div`
    padding: 10px 0 0 7px;
    font-weight: bolder;
    font-size: 18px;
`;
